        <div class="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
            <div class="flex items-center justify-between space-x-4">
                <h2 class="text-3xl font-medium text-gray-900 mb-10">Vidéos de formation</h2>
            </div>
            <div class="mt-6 grid grid-cols-1 gap-x-8 gap-y-14 sm:grid-cols-2 sm:gap-y-10 lg:grid-cols-4">
                <div v-for="product in products" :key="product.id" class="group relative">
                    <div class="relative">
                        <img :src="product.imageSrc" :alt="product.imageAlt" class="aspect-[4/3] w-full rounded-lg bg-gray-100 object-cover" />
                        <div class="absolute inset-0 flex items-end p-4 opacity-0 group-hover:opacity-100" aria-hidden="true">
                            <div  class="w-full rounded-md bg-white/75 px-4 py-2 text-center text-sm font-medium text-gray-900 backdrop-blur backdrop-filter">Voir la vidéo</div>
                        </div>
                    </div>
                    <div class="mt-4 flex items-center justify-center space-x-8 text-base font-medium text-gray-900">
                        <h3 class="text-center">
                            <a class="text-center" href="#">
                                <span @click="viewVideo(product.href)" aria-hidden="true" class="absolute inset-0" />
                                {{ product.name }}
                            </a>
                        </h3>
                    </div>
                </div>
            </div>
        </div>

        <TransitionRoot appear :show="modalOpen" as="div" enter="duration-300 ease-out" enter-from="opacity-0" enter-to="opacity-100" leave="duration-200 ease-in" leave-from="opacity-100" leave-to="opacity-0">
